package Exercise;

public class UnaryDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 3;
		i++;
		//print 4
		System.out.println(i);
		++i;
		//print 5
		System.out.println(i);
		//print 6
		System.out.println(++i);
		//print 6
		System.out.println(i++);
		//print 7
		System.out.println(i);
	}

}
