package fa.training.assignment1;

public class RectangExercise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float chieuRong = 5.5f;
		float chieuCao = 8.5f;
		
		System.out.println("Dien tich la: "+ chieuRong +" * "+chieuCao+" = "+ (chieuRong*chieuCao));
		System.out.printf("Chu vi la 2 *" + "(" + chieuRong+"+" + chieuCao +")"+ "="+ "%.2f",(2*((chieuCao+chieuRong))));
	}

}
